	public void printMessage(String message) {
		for (char ch: message.toCharArray()) {
			System.out.print(ch);
			waitForTime(20);
		}
		System.out.print("\r\n");
	}
	public void printToDisplay(String message) {
		ArrayList<String> messageArray = new ArrayList<String>();;
		int i = 1;
		float iCounter = message.length()/84;
		if (iCounter > 0) {
			do {
				messageArray.add("| " + message.substring((83*i)-83, (83*i)) + " |");
				i += 1;
			} while (i <= iCounter);
			messageArray.add("| " + padRight((message.substring( (83*i)-83, message.length())), 84)+ "|");
		} else {
			messageArray.add("| " + padRight(message, 84) + "|");
		}
		message =  "+-------------------------------------------------------------------------------------+\r\n";
		for (String str : messageArray) {
			message = message + str + "\r\n";
		}

		for (int z = 0; z <= (21-i); z++) {
			message = message + "|                                                                                     |\r\n";
		}
		message = message + "+-------------------------------------------------------------------------------------+";
		System.out.println(message);
	}
